import { useLoaderData } from "react-router-dom";
import { Link } from "react-router-dom";

function EventPage() {
  const data = useLoaderData();
  const events = data.events; 
  console.log(events)

  return (
    <>
      <h1>Event Page</h1>
      <ul>
        {events.length > 0 ? (
          events.map((event) => (
            <li key={event.id}>
              <Link to={event.id}>{event.title} </Link> <img style={
                {
                    width:'200px',
                    height:'200px'
                }
              } src={event.img} alt="" />
            </li>
          ))
        ) : (
          <p>No events found.</p>
        )}
      </ul>
    </>
  );
}

export default EventPage;
