import { useLoaderData } from "react-router-dom";

function EventDetailPage() {
  const data = useLoaderData();
  const event = data.event; // Make sure this matches the API response

  return (
    <div>
      <h1>{event.title}</h1>
      <img src={event.image} alt={event.title} width="300" />
      <p>{event.description}</p>
      <p>Date: {event.date}</p>
    </div>
  );
}

export default EventDetailPage;

export async function loader({ params }) {
  const response = await fetch(`http://192.168.1.25:8080/events/${params.id}`);

  if (!response.ok) {
    throw new Response(JSON.stringify({ message: "Could not fetch event!" }), {
      status: 500,
    });
  }

  const data = await response.json();
  return data; // Ensure it returns { event: {...} }
}
