import { useLoaderData } from "react-router-dom";
import { Link } from "react-router-dom";
import classes from "./EventPage.module.css"; // Import CSS module
import { useState } from "react";
function EventPage() {
  const data = useLoaderData();
  const events = data.events; 
  // console.log(events)
// const [query,setQuery]=useState('')
// const filteredEvents = events.filter((event) =>
  // event.title.toLowerCase().includes(query.toLowerCase())
// );



  const [searchitem,getsearchitem]=useState()

const filtered=events.filter(events=>{
  events.title.toLowerCase().includes(searchitem.toLowerCase())
})

  return (
    <> <div className={classes.events}></div>
      <h1>Event Page</h1>
      <input type="text" 
        onChange={(e) => setQuery(e.target.value)}
        />
      <ul> 
        {filtered.length > 0 ? (
          filtered.map((event) => (
            <li key={event.id} className={classes.item}>
              <Link to={event.id} >
              <div className={classes.content} >
              {event.title}<img style={
                {
                    width:'200px',
                    height:'200px'
                }
              } src={event.image} alt="" /> 
              </div>
              </Link> 
            </li>
          ))
        ) : (
          <p>No events found.</p>
        )}
      </ul>
    </>
  );
}

export default EventPage;
