import EventForm from "../components/EventForm";

function NewEventPage() {
  return (
    <>
     

      <p>shafiq</p>
      <EventForm event={{}} /> 
    </>
  );
}

export default NewEventPage;
export async function action({ request }) {
    // ✅ Get form data properly
    const data = await request.formData();
  
    // ✅ Extract form values correctly
    const eventdata = {
      title: data.get('title'),
      image: data.get('image'),
      date: data.get('date'),
      description: data.get('description'),
    };
  
    // ✅ Send request properly
    const response = await fetch('http://192.168.1.25:8080', {
      method: 'POST',  // ✅ Corrected method
      headers: {
        'Content-Type': 'application/json',  // ✅ Added required headers
      },
      body: JSON.stringify(eventdata),  // ✅ Converted to JSON
    });
  
    // ✅ Check for errors
    if (!response.ok) {
      throw new Error('Failed to send event data');
    }
  
    return response.json(); // ✅ Return server response
  }
