import { useLoaderData } from "react-router-dom";
import { Link } from "react-router-dom";
import classes from "./EventPage.module.css"; // Import CSS module
import { useState } from "react";

function EventPage() {
  const data = useLoaderData();
  const events = data.events || []; // ✅ Ensure it's always an array

  const [query, setQuery] = useState(""); // ✅ Track search input

  // ✅ Filter events based on the search query
  const filteredEvents = events.filter((event) =>
    event.title.toLowerCase().includes(query.toLowerCase()) // ✅ Case-insensitive search
  );

  return (
    <>
      <h1>Event Page</h1>

      {/* ✅ Search Input */}
      <input
        type="text"
        placeholder="Search events..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      <ul className={classes.events}>
        {filteredEvents.length > 0 ? (
          filteredEvents.map((event) => (
            <li key={event.id} className={classes.item}>
              <Link to={event.id}>
                <div className={classes.content}>
                  {event.title}
                  <img
                    style={{
                      width: "200px",
                      height: "200px",
                    }}
                    src={event.image}
                    alt=""
                  />
                </div>
              </Link>
            </li>
          ))
        ) : (
          <p>No events found.</p>
        )}
      </ul>
    </>
  );
}

export default EventPage;
